mapper.select:
SELECT
accom_number
FROM rooms where room_number = #{roomNumber}

SELECT point
FROM user
WHERE id = #{Id}

mapper.update:
UPDATE user
SET point = point - #{usePoint}
WHERE id =
#{Id}

mapper.select:
SELECT r.id, r.use_point, r.coupon_number, r.payment_amount
FROM reservation r
WHERE r.reservation_number = #{reservationNumber}

mapper.update:
UPDATE user
SET point = point + #{usePoint}
WHERE id =
#{Id}

mapper.delete:
DELETE FROM reservation
WHERE status = 0
AND TIMESTAMPDIFF(SECOND, reservation_time, NOW()) >= 600;
